function FactList({ currentCategory, facts, setFacts, searchQuery, sortBy, showDisputed, showPopular }) {
  let filteredFacts = facts;

  // Category filter
  if (currentCategory !== "all") {
    filteredFacts = filteredFacts.filter((fact) => fact.category === currentCategory);
  }

  // Search filter
  if (searchQuery) {
    filteredFacts = filteredFacts.filter((fact) =>
      fact.text.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }

  // Disputed filter
  if (showDisputed) {
    filteredFacts = filteredFacts.filter(
      (fact) => fact.votesInteresting + fact.votesMindblowing < fact.votesFalse
    );
  }

  // Popular filter
  if (showPopular) {
    filteredFacts = filteredFacts.filter(
      (fact) => fact.votesInteresting + fact.votesMindblowing > 20
    );
  }

  // Sort facts
  filteredFacts = [...filteredFacts].sort((a, b) => {
    switch (sortBy) {
      case "newest":
        return new Date(b.created_at) - new Date(a.created_at);
      case "oldest":
        return new Date(a.created_at) - new Date(b.created_at);
      case "most_votes":
        return (
          b.votesInteresting + b.votesMindblowing + b.votesFalse -
          (a.votesInteresting + a.votesMindblowing + a.votesFalse)
        );
      case "least_votes":
        return (
          a.votesInteresting + a.votesMindblowing + a.votesFalse -
          (b.votesInteresting + b.votesMindblowing + b.votesFalse)
        );
      case "most_interesting":
        return b.votesInteresting - a.votesInteresting;
      case "most_mindblowing":
        return b.votesMindblowing - a.votesMindblowing;
      default:
        return 0;
    }
  });

  if (filteredFacts.length === 0)
    return (
      <p className="message">
        No facts found! Try adjusting your filters or create a new fact.
      </p>
    );

  return (
    <section>
      <ul className="facts-list">
        {filteredFacts.map((fact) => (
          <Fact key={fact.id} fact={fact} setFacts={setFacts} />
        ))}
      </ul>
      <p>
        Showing {filteredFacts.length} facts. Add your own!
      </p>
    </section>
  );
}

function Fact({ fact, setFacts }) {
  const [isUpdating, setIsUpdating] = useState(false);
  const isDisputed =
    fact.votesInteresting + fact.votesMindblowing < fact.votesFalse;

  async function handleVote(columnName) {
    setIsUpdating(true);
    const { data: updatedFact, error } = await supabase
      .from("facts")
      .update({ [columnName]: fact[columnName] + 1 })
      .eq("id", fact.id)
      .select();
    setIsUpdating(false);

    if (!error && updatedFact) {
      setFacts((facts) =>
        facts.map((f) => (f.id === fact.id ? updatedFact[0] : f))
      );
    }
  }

  return (
    <li className="fact">
      <p>
        {isDisputed && <span className="disputed">[⛔ DISPUTED]</span>}
        {fact.text}
        <a
          className="source"
          href={fact.source}
          target="_blank"
          rel="noreferrer"
        >
          (Source)
        </a>
      </p>
      <span
        className="tag"
        style={{
          backgroundColor: CATEGORIES.find((cat) => cat.name === fact.category)
            ?.color,
        }}
      >
        {fact.category}
      </span>
      <div className="vote-buttons">
        <button
          onClick={() => handleVote("votesInteresting")}
          disabled={isUpdating}
        >
          👍 {fact.votesInteresting}
        </button>
        <button
          onClick={() => handleVote("votesMindblowing")}
          disabled={isUpdating}
        >
          🤯 {fact.votesMindblowing}
        </button>
        <button onClick={() => handleVote("votesFalse")} disabled={isUpdating}>
          ⛔ {fact.votesFalse}
        </button>
      </div>
    </li>
  );
}

